import { Component, OnInit } from '@angular/core';
import { DataService } from 'src/app/services/data.service';
import { ModalService } from 'src/app/services/modal.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  boards: any = this.service.boards;
  showModal = this.modalService.showModal;
  showModalBoard: any = this.modalService.showModalBoard;
  // editButton = this.modalService.showEditButton;

  constructor(private service: DataService,
    private modalService: ModalService) {
    this.addItem = this.addItem.bind(this)
    this.editItem = this.editItem.bind(this)
    setInterval(() => {
      console.log('fof')
      this.boards = this.service.filteredItems
    }, 1000);
  }

  ngOnInit(): void {
  }

  createBoard(evt: any) {
    this.showModal = evt;
  }

  addItem(title: string | never) {
    this.service.setData({ 'title': title });
  }

  editItem(title: any, input: any) {
    console.log(title, input)
    this.service.editData({ 'title': title }, input);
  }

  // showEdit(event:any){
  //   console.log(event)
  //   this.editButton = true;
  // }

}
